package com.honchar.onlinecinema.presentation.home

import androidx.lifecycle.LiveData
import androidx.lifecycle.MutableLiveData
import com.honchar.onlinecinema.core.base.presentation.BaseViewModel
import com.honchar.onlinecinema.core.views.FilmsCategory
import com.honchar.onlinecinema.core.views.FilmsCategory.FilmCategory

abstract class HomeViewModel: BaseViewModel(){
    abstract val itemsLiveData: LiveData<List<FilmCategory>>
    abstract val topFilmsLiveData: LiveData<List<FilmsCategory.Film>>

    abstract fun getItems()
}

class HomeViewModelImpl : HomeViewModel() {
    override val itemsLiveData = MutableLiveData<List<FilmCategory>>()
    override val topFilmsLiveData = MutableLiveData<List<FilmsCategory.Film>>()

    private val film = FilmsCategory.Film(
        filmId = "1",
        filmName = "Dune",
        filmPoster = "data:image/jpeg;base64,",
        filmRate = "3.8",
        filmViews = "23M views",
        placeholder = 0,
        filmRelease = "January 29"
    )

    override fun getItems() {
        val list = listOf(
            FilmCategory(
                filmCategoryTitle = "Most Popular films",
                films = listOf(film.copy(filmId = "2"), film.copy(filmId = "3"), film.copy(filmId = "4"))
            ),
            FilmCategory(
                filmCategoryTitle = "Worst Popular films",
                films = listOf(film.copy(filmId = "5"),film.copy(filmId = "6"),film.copy(filmId = "7"),film.copy(filmId = "8"),film.copy(filmId = "29"),film.copy(filmId = "10"))
            ),
            FilmCategory(
                filmCategoryTitle = "Popular films",
                films = listOf(film,film,film,film,film,film, film,film,film,film)
            ),
            FilmCategory(
                filmCategoryTitle = "1 Popular films",
                films = listOf(film,film,film)
            ),
            FilmCategory(
                filmCategoryTitle = "2 Popular films",
                films = listOf(film,film,film,film,film,film,)
            ),
            FilmCategory(
                filmCategoryTitle = "3 films",
                films = listOf(film,film,film,film,film,film, film,film,film,film)
            )
        )
        itemsLiveData.postValue(list)
        topFilmsLiveData.postValue(listOf(film.copy(filmId = "11"), film.copy(filmId = "12"), film.copy(filmId = "13")))
    }
}